import './App.css';
import Keyboard from './components/Keyboard';
import Board from './components/Board';
import GameOver from './components/GameOver';
import { boardDefault, generateWordSet } from './Words';
import { createContext, useEffect, useState } from 'react';
import Modal from './components/Modal';

export const AppContext = createContext();

function App() {
  const [board, setBoard] = useState(boardDefault);
  const [currAttempt, setCurrAttempt] = useState({ attempt: 0, letterPos: 0 });
  const [wordSet, setWordSet] = useState(new Set());
  const [disabledLetters, setDisabledLetters] = useState([]);
  const [correctWord, setCoreectWord] = useState('');
  const [gameOver, setGameOver] = useState({gameOver: false, guessedWord: false});
  const [show, setShow] = useState(true);
  const [guessedWords, setGuessedWords] = useState([]);

  useEffect(() =>{
    generateWordSet().then((words) =>{
      setWordSet(words.wordSet);
      setCoreectWord(words.todaysWord);
    })
  }, [])

  const onSelectLetter = (keyVal) => {
    if (currAttempt.letterPos > 4 || show) return;
    const newBoard = [...board]
    newBoard[currAttempt.attempt][currAttempt.letterPos] = keyVal
    setBoard(newBoard)
    setCurrAttempt({...currAttempt, letterPos: currAttempt.letterPos + 1});
  }; 

  const onDelete = () => {
    if(currAttempt.letterPos === 0 || show) return;
    const newBoard = [...board];
    newBoard[currAttempt.attempt][currAttempt.letterPos - 1] = "";
    setBoard(newBoard)
    setCurrAttempt({...currAttempt, letterPos: currAttempt.letterPos - 1 });
  };

  const onEnter = () => {
    if(currAttempt.letterPos !== 5)return;

    const currWord = "";


    for(let i = 0; i < 5; i++){
      currWord += board[currAttempt.attempt][i];
    }

    //currWord = board[currAttempt.attempt].join('').toLowerCase();

    if (guessedWords.has(currWord)) {
      alert('You have already guessed this word!');
      return;
    }

    if (wordSet.has(currWord.toLocaleLowerCase())){
      setCurrAttempt({ attempt: currAttempt.attempt + 1, letterPos: 0 });
      //setGuessedWords(new Set([...guessedWords, currWord]));
    }else{
      alert('word not found');
    }

    if (currWord === correctWord){
      setGameOver({gameOver: true, guessedWord: true})
      return;
    }

    if(currAttempt.attempt === 5){
      setGameOver({gameOver:true, guessedWord: false})
    }

  };

  return (
    <div className="App">
      <nav className='navbar'>
        <h1>Wordle</h1>
        <button className="help-button" onClick={() => setShow(true)} show={show} >
        <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" class="bi bi-patch-question" viewBox="0 0 16 16"> <path d="M8.05 9.6c.336 0 .504-.24.554-.627.04-.534.198-.815.847-1.26.673-.475 1.049-1.09 1.049-1.986 0-1.325-.92-2.227-2.262-2.227-1.02 0-1.792.492-2.1 1.29A1.71 1.71 0 0 0 6 5.48c0 .393.203.64.545.64.272 0 .455-.147.564-.51.158-.592.525-.915 1.074-.915.61 0 1.03.446 1.03 1.084 0 .563-.208.885-.822 1.325-.619.433-.926.914-.926 1.64v.111c0 .428.208.745.585.745z" fill="white"></path> <path d="m10.273 2.513-.921-.944.715-.698.622.637.89-.011a2.89 2.89 0 0 1 2.924 2.924l-.01.89.636.622a2.89 2.89 0 0 1 0 4.134l-.637.622.011.89a2.89 2.89 0 0 1-2.924 2.924l-.89-.01-.622.636a2.89 2.89 0 0 1-4.134 0l-.622-.637-.89.011a2.89 2.89 0 0 1-2.924-2.924l.01-.89-.636-.622a2.89 2.89 0 0 1 0-4.134l.637-.622-.011-.89a2.89 2.89 0 0 1 2.924-2.924l.89.01.622-.636a2.89 2.89 0 0 1 4.134 0l-.715.698a1.89 1.89 0 0 0-2.704 0l-.92.944-1.32-.016a1.89 1.89 0 0 0-1.911 1.912l.016 1.318-.944.921a1.89 1.89 0 0 0 0 2.704l.944.92-.016 1.32a1.89 1.89 0 0 0 1.912 1.911l1.318-.016.921.944a1.89 1.89 0 0 0 2.704 0l.92-.944 1.32.016a1.89 1.89 0 0 0 1.911-1.912l-.016-1.318.944-.921a1.89 1.89 0 0 0 0-2.704l-.944-.92.016-1.32a1.89 1.89 0 0 0-1.912-1.911l-1.318.016z" fill="white"></path> <path d="M7.001 11a1 1 0 1 1 2 0 1 1 0 0 1-2 0z" fill="white"></path> </svg>
        </button>
        <Modal onClose={() => setShow(false)} show={show} />
      </nav>
      <AppContext.Provider value={{ board, setBoard, currAttempt, setCurrAttempt, onSelectLetter, onDelete, onEnter, correctWord, disabledLetters, setDisabledLetters, gameOver, setGameOver }}>

        <div className='game'>
        <Board/>
        {gameOver.gameOver ? (
            <GameOver />
          ) : (
            <Keyboard disabled={show} />
        )}  
        </div>
      </AppContext.Provider>

    </div>
  );
}

export default App;
