import React, { useCallback, useEffect, useContext, useState } from 'react';
import Key from './Key';
import { AppContext } from '../App';

function Keyboard() {
  const keys1 = ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'];
  const keys2 = ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'];
  const keys3 = ['Z', 'X', 'C', 'V', 'B', 'N', 'M'];

  const allKeys = [...keys1, ...keys2, ...keys3];

  const { onSelectLetter, onDelete, onEnter, disabledLetters } = useContext(AppContext);

  const [pressedKey, setPressedKey] = useState(null); // Add state variable

  // Handle keyboard events
  const handleKeyboard = useCallback((event) => {
    if (event.key === 'Enter') {
      onEnter();
    } else if (event.key === 'Backspace') {
      onDelete();
    } else {
      if (allKeys.join('').includes(event.key.toUpperCase())) {
        onSelectLetter(event.key.toUpperCase());
        setPressedKey(event.key.toUpperCase()); // Set pressed key in state
      }
    }
  });

  // Add event listener for keydown event
  useEffect(() => {
    document.addEventListener('keydown', handleKeyboard);
    return () => {
      document.removeEventListener('keydown', handleKeyboard);
    };
  }, [handleKeyboard]);

  // Add event listener for keyup event to remove pressed key from state
  useEffect(() => {
    const handleKeyUp = (event) => {
      if (allKeys.join('').includes(event.key.toUpperCase())) {
        setPressedKey(null);
      }
    };
    document.addEventListener('keyup', handleKeyUp);
    return () => {
      document.removeEventListener('keyup', handleKeyUp);
    };
  }, [allKeys]);

  return (
    <div className='keyboard'>
      <div className='line1'>
        {keys1.map((key) => {
          return <Key keyVal={key} disabled={disabledLetters.includes(key)} className={pressedKey === key ? 'pressed' : ''} />;
        })}
      </div>
      <div className='line2'>
        {keys2.map((key) => {
          return <Key keyVal={key} disabled={disabledLetters.includes(key)} className={pressedKey === key ? 'pressed' : ''} />;
        })}
      </div>
      <div className='line3'>
        <Key keyVal={'ENTER'} bigKey />
        {keys3.map((key) => {
          return <Key keyVal={key} disabled={disabledLetters.includes(key)} className={pressedKey === key ? 'pressed' : ''} />;
        })}
        <Key keyVal={'DELETE'} bigKey className={pressedKey === 'Backspace' ? 'pressed' : ''} />
      </div>
    </div>
  );
}

export default Keyboard;
